mapper.select:

select  a.gdsNum, a.gdsName, a.gdsPrice, a.cateParent, a.gdsStock, a.gdsDate
from tbl_goods AS a
left join tbl_cate2 AS e on a.cateParent = e.cateName;



	where (gdsName like concat ('%', #{keyword}, '%'))

order by gdsNum DESC
limit #{skip}, #{amount};

mapper.update:

update tbl_goods set gdsName = #{gdsName}, cateName = ${cateName}, cateParent = ${cateParent}, gdsSize = ${gdsSize}, gdsColor = ${gdsColor},
	gdsPrice = #{gdsPrice}, gdsStock = #{gdsStock}, gdsDes = #{gdsDes}
where gdsNum = ${gdsNum}
