mapper.select:
select a.gdsNum, a.gdsName, a.gdsPrice, a.cateCode, a.gdsStock, a.gdsDate from tbl_goods AS a left join tbl_cate1 AS b on a.cateCode = b.cateName left join tbl_cate2 AS c on b.cateName = c.cateName
where (gdsName like concat ('%', #{keyword}, '%'))
order by gdsNum DESC limit #{skip}, #{amount};


	select gdsNum, gdsName,
		cateCode, gdsPrice, gdsStock, gdsDes, gdsDate 
	from tbl_goods where gdsNum = #{gdsNum}

mapper.update:

update tbl_goods set gdsName = #{gdsName}, cateName = ${cateName}, cateCode = ${cateCode}, gdsPrice = #{gdsPrice}, 
	gdsStock = #{gdsStock}, gdsDes = #{gdsDes}
where gdsNum = ${gdsNum}
